  Widget btnContainer({required Color clr,required String svg,required String title,required int index}){
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: GestureDetector(
        onTap: (){
          if(index==0 || index==1)
            {
              Navigator.push(context, MaterialPageRoute(builder: (builder)=>EnrollUpdateScreen(index: index)));
            }
          else if(index==2)
            {
              Navigator.push(context, MaterialPageRoute(builder: (builder)=>const FeedBackForm()));
            }
        },
        child: Container(
          padding: const EdgeInsets.symmetric(vertical:20),
          decoration: BoxDecoration(
            color: clr,
            borderRadius: BorderRadius.circular(16),
          ),
          child:Column(
            mainAxisAlignment: MainAxisAlignment.center,
            mainAxisSize: MainAxisSize.min,
            children: [
              SvgPicture.string(svg),
              const SizedBox(height: 5,),
              Row(
                mainAxisAlignment: MainAxisAlignment.center,

                children: [
                  Flexible(child: CustomTextWidget(txt:title, clr: const Color(0XFF464444),fontSize:14,maxLines: 2,)),
                ],
              )
            ],
          ),
        ),
      ),
    );
  }
